import { FC } from "react";
import "./Answers.css";
import { AnswerData } from "../../pages/Game/Game.types";
import Results from "../Results/Results";
import { updateAnswer } from "../../requests/updateAnswer";
import {useLocalStorageState} from "../../hooks/useLocalStorage"

type AnswersProps = {
  answers: AnswerData[];
  scores?: AnswerData[];
};

const Answers: FC<AnswersProps> = ({ answers, scores }) => {

  const [answers1, setAnswers] = useLocalStorageState<AnswerData[]>(
    'answer',
    [
      {
        id: 0,
        answer: '',
        isCorrect: false,
      },
    ]
  );

  const handleSetAnswer = () => {
    if (scores !== null) {
      setAnswers((prevAnswers) => [...prevAnswers, scores]);
    }
  };


  console.log(answers)


  console.log(process.env);
  console.log(scores)
  return (
    <div>
      {scores && scores.length <= 9 && (
        <div className="button-container">
          {answers.map((answer, index) => {
            return (
              <button
                className="button"
                key={`${answer.id}=${index}`}
                onClick={() => {updateAnswer(answer)
                  handleSetAnswer()}
              }
              >
                {answer.answer}
              </button>
            );
          })}
        </div>
      )}
      <div>{scores && scores.length >= 10 && <Results scores={scores} />}</div>
    </div>
  );
};

export default Answers;
