import { FC } from "react";
import "./Answers.css";
import { AnswerData } from "../../pages/Game/Game.types";
import Results from "../Results/Results";
import { updateAnswer } from "../../requests/updateAnswer";
import {useLocalStorageState} from "../../hooks/useLocalStorage"

type AnswersProps = {
  answers: AnswerData[];
  scores?: AnswerData[];
};

const Answers: FC<AnswersProps> = ({ answers, scores }) => {



  const handleSetAnswer = (answer: AnswerData) => {
   const existingValue = localStorage.getItem("answerArray");

    let test = existingValue ? JSON.parse(existingValue) : [];

    localStorage.setItem("answerArray", JSON.stringify([answer, ...test]))  
  };


  console.log(scores, 'SCORES')
  return (
    <div>
      {scores && scores.length <= 9 && (
        <div className="button-container">
          {answers.map((answer, index) => {
            return (
              <button
                className="button"
                key={`${answer.id}=${index}`}
                onClick={() => {updateAnswer(answer)
                  handleSetAnswer(answer)
                }}>
                {answer.answer}
              </button>
            );
          })}
        </div>
      )}
      <div>{scores && scores.length >= 10 && <Results scores={scores} />}</div>
    </div>
  );
};

export default Answers;
